<?php	require_once 'Database.php';	$db = new Database();		if($_SERVER['REQUEST_METHOD'] == 'POST'){		$new_info = [];		$new_info['member_username'] = $_POST['name'];		$new_info['member_email'] = $_POST['name'];		$new_info['member_contact'] = $_POST['phone'];		$new_info['member_address'] = $_POST['address'];		$db -> table('member') -> where('member_id',$_POST['id']) -> update($new_info);	}		header('Location: ../member/edit_profile');	exit();?>